export const isAudio = (mimeType: string) => mimeType.startsWith('audio/')
export const isVideo = (mimeType: string) => mimeType.startsWith('video/')
export const isImage = (mimeType: string) => mimeType.startsWith('image/')

// Basic Scrap Helpers
export const getTitleOfDoc = (htmlDoc: any) => {
  const titleEl = htmlDoc.querySelector('title')
  if (!titleEl) {
    return null
  }
  return titleEl.innerText
}

export const getAttrOfDocElement = (htmlDoc: any, query: string, attr: string) => {
  const el = htmlDoc.querySelector(query)
  if (!el) {
    return null
  }
  return el.getAttribute(attr)
}

export const getYoutTubeVideoId = (url: string) => {
  const parsed = url.match(/^.*((m\.)?youtu\.be\/|vi?\/|u\/\w\/|embed\/|\?vi?=|\&vi?=)([^#\&\?]*).*/)

  if (parsed && parsed[3]) {
    return parsed[3]
  } else {
    return null
  }
}

export const getInstagramVideo = (htmlDoc: any) => {
  const videoLinkMatcher = /(?:"video_url":")(.*?)(?:")/g
  if (videoLinkMatcher.test(htmlDoc)) {
    const videoMatches = videoLinkMatcher.exec(htmlDoc)
    if (videoMatches && videoMatches.length !== 0) {
      return videoMatches[0].split('":"')[1].replace('"', '')
    }
  }
  return null
}

export const fixRelativeUrls = (baseUrl: string, itemUrl: string) => {
  const normalizedUrl = itemUrl.toLocaleLowerCase()
  if (normalizedUrl.startsWith('http://') || normalizedUrl.startsWith('https://')) {
    return itemUrl
  }
  return new URL(itemUrl, baseUrl).href
}

export const getBaseUrl = (htmlDoc: any, url: string) => getAttrOfDocElement(htmlDoc, 'base', 'href') || new URL(url).origin
import { css } from 'styled-components';
import { IReactTinyLinkData, ReactTinyLinkType } from './ReactTinyLinkTypes';

const REGEX_STRIP_WWW = /^www\./;
export const media = {
  mobile: (...args) => css`
    @media (max-width: 48em) {
      ${css(...args)};
    }
  `,
  desktop: (...args) => css`
    @media (max-width: 48em) {
      ${css(...args)};
    }
  `,
};

export const getHostname = href => {
  const { hostname } = new URL(href);
  return hostname.replace(REGEX_STRIP_WWW, '');
};

export const isLarge = cardSize => cardSize === 'large';

export const isValidImageURL = src => {
  if (typeof src !== 'string') return false;
  return !!src.match(/\w+\.(a?png|gif|jpe?g|svg|webp)/gi)
}

export const isValidVideoURL = src => {
  if (typeof src !== 'string') return false;
  return !!src.match(/\w+\.(mp4|ogg|webm)/gi);
}

export const findFirstSecureUrl = (records, condition) => {
  const result = records.find(record => condition(record) && record.startsWith('https://'));
  return result ? result : '';
}

/**
 * @returns {IReactTinyLinkData} with default values.
 *
 * @param url url to fetch
 * @param defaultMedia media assigned to both the image and the video
 */
export function defaultData(url: string, defaultMedia: string[]): IReactTinyLinkData {
    return {
        title: url.substring(url.lastIndexOf('/') + 1),
        description: url.substring(url.lastIndexOf('/') + 1),
        image: defaultMedia,
        url: url,
        video: defaultMedia,
        type: ReactTinyLinkType.TYPE_DEFAULT,
    }
}

