    return {
      title: htmlDoc.querySelector('title').innerText,
      url: url,
      description: url,
      type: ReactTinyLinkType.TYPE_YOUTUBE,
      video: [],
      image: !defaultMedia ? image.filter(i => !isEmpty(i)) : [...image, defaultMedia].filter(i => !isEmpty(i)),
    }
  }
}
