export function parseRange(input: DateRangeInput, dateEnv: DateEnv): OpenDateRange {
  let start = null
  let end = null

  if (input.start) {
    start = dateEnv.createMarker(input.start)
  }

  if (input.end) {
    end = dateEnv.createMarker(input.end)
  }

  if (!start && !end) {
