/*
An object for getting/setting scroll-related information for an element.
Internally, this is done very differently for window versus DOM element,
so this object serves as a common interface.
*/
export abstract class ScrollController {
  abstract getScrollTop(): number
  abstract getScrollLeft(): number
  abstract setScrollTop(top: number): void
  abstract setScrollLeft(left: number): void
  abstract getClientWidth(): number
  abstract getClientHeight(): number
  abstract getScrollWidth(): number
  abstract getScrollHeight(): number

  getMaxScrollTop() {
    return this.getScrollHeight() - this.getClientHeight()
  }

  getMaxScrollLeft() {
    return this.getScrollWidth() - this.getClientWidth()
  }

  canScrollVertically() {
    return this.getMaxScrollTop() > 0
  }

  canScrollHorizontally() {
    return this.getMaxScrollLeft() > 0
  }

  canScrollUp() {
    return this.getScrollTop() > 0
  }

  canScrollDown() {
    return this.getScrollTop() < this.getMaxScrollTop()
  }

  canScrollLeft() {
    return this.getScrollLeft() > 0
  }

  canScrollRight() {
    return this.getScrollLeft() < this.getMaxScrollLeft()
  }
}

export class ElementScrollController extends ScrollController {
  el: HTMLElement

  constructor(el: HTMLElement) {
    super()
    this.el = el
  }

  getScrollTop() {
    return this.el.scrollTop
  }

  getScrollLeft() {
    return this.el.scrollLeft
  }

  setScrollTop(top: number) {
    this.el.scrollTop = top
  }

  setScrollLeft(left: number) {
    this.el.scrollLeft = left
  }

  getScrollWidth() {
    return this.el.scrollWidth
  }

  getScrollHeight() {
    return this.el.scrollHeight
  }

  getClientHeight() {
    return this.el.clientHeight
  }

  getClientWidth() {
    return this.el.clientWidth
  }
}

export class WindowScrollController extends ScrollController {
  getScrollTop() {
    return window.pageYOffset
  }

  getScrollLeft() {
    return window.pageXOffset
  }

  setScrollTop(n: number) {
    window.scroll(window.pageXOffset, n)
  }

  setScrollLeft(n: number) {
    window.scroll(n, window.pageYOffset)
  }

  getScrollWidth() {
    return document.documentElement.scrollWidth
  }

  getScrollHeight() {
    return document.documentElement.scrollHeight
  }

  getClientHeight() {
    return document.documentElement.clientHeight
  }

  getClientWidth() {
    return document.documentElement.clientWidth
  }
}
import { CalendarOptionsRefined } from '../options'

export class Theme {
  // settings. default values are set after the class
  classes: any
  iconClasses: any
  rtlIconClasses: any
  baseIconClass: string // className that ALL icon elements for this theme should have
  iconOverrideOption: any // the name of the setting to use for icons. the subclass must set this.
  iconOverrideCustomButtonOption: any // the name of the setting, *within* each customButtons object, to use for icons
  iconOverridePrefix: string

  constructor(calendarOptions: CalendarOptionsRefined) {
    if (this.iconOverrideOption) {
      this.setIconOverride(
        calendarOptions[this.iconOverrideOption],
      )
    }
  }

  setIconOverride(iconOverrideHash) {
    let iconClassesCopy
    let buttonName

    if (typeof iconOverrideHash === 'object' && iconOverrideHash) { // non-null object
      iconClassesCopy = { ...this.iconClasses }

      for (buttonName in iconOverrideHash) {
        iconClassesCopy[buttonName] = this.applyIconOverridePrefix(
          iconOverrideHash[buttonName],
        )
      }

      this.iconClasses = iconClassesCopy
    } else if (iconOverrideHash === false) {
      this.iconClasses = {}
    }
  }

  applyIconOverridePrefix(className) {
    let prefix = this.iconOverridePrefix

    if (prefix && className.indexOf(prefix) !== 0) { // if not already present
      className = prefix + className
    }

    return className
  }

  getClass(key) {
    return this.classes[key] || ''
  }

  getIconClass(buttonName, isRtl?: boolean) {
    let className

    if (isRtl && this.rtlIconClasses) {
      className = this.rtlIconClasses[buttonName] || this.iconClasses[buttonName]
    } else {
      className = this.iconClasses[buttonName]
    }

    if (className) {
      return `${this.baseIconClass} ${className}`
    }

    return ''
  }

  getCustomButtonIconClass(customButtonProps) {
    let className

    if (this.iconOverrideCustomButtonOption) {
      className = customButtonProps[this.iconOverrideCustomButtonOption]

      if (className) {
        return `${this.baseIconClass} ${this.applyIconOverridePrefix(className)}`
      }
    }

    return ''
  }
}

Theme.prototype.classes = {}
Theme.prototype.iconClasses = {}
Theme.prototype.baseIconClass = ''
Theme.prototype.iconOverridePrefix = ''

