import React, {useState, useEffect} from "react";
import "./styles.css";
import {db, Fire} from './Fire'
import firebase from 'firebase'
import { BrowserRouter as Router,Switch,Route,Link,NavLink, Redirect } from "react-router-dom" 
import Sidebar from './components/Sidebar'
import Login from './components/Login'
import Body from './components/Body'
import ContextAppProvider from './ContextAPI'
import { useBeforeunload } from 'react-beforeunload'
function App() {
  const [update, setUpdate]=useState(0)
  const [user, setUser]=useState('')
  const [name, setName]=useState('')
  const [email, setEmail]=useState('')
  const [password, setPassword]=useState('')
  const [emailError, setEmailError]=useState('')
  const [passwordError, setPasswordError]=useState('')
  const [hasAccount, setHasAccount]=useState(false)
  const [lname, setlName]=useState('')
  const [userinfo, setUserinfo]=useState([])
  const [users, setUsers]=useState([])
  const [cover, setCover]=useState('https://www.gettyimages.ca/gi-resources/images/500px/983794168.jpg')
  const [forgotpassword, setForgotpassword]=useState(false)
  const [msgids, setMsgIds] = useState([''])
  const [loading, setLoading]=useState(false)
  const clearInputs = () => {
    setEmail('')
    setPassword('')
  }
  const clearErrors = () => {
    setEmailError('')
    setPasswordError('')
  }
  const handleLogin = () => {

    clearErrors()
    firebase.auth().signInWithEmailAndPassword(email, password)
    .then(()=>{setLoading(true)})
    .catch(err => {
      switch(err.code) {
        case "auth/invalid-email":
          setEmailError(err.message)
          break
        case "auth/user/disabled":
        case "auth/user-not-found":
          setEmailError('Email does not exist')
        break
        case "auth/wrong-password":
          setPasswordError('Incorrect Password')
        break
        default:
      }  
    })

  } 
  const handleSignup = () => {
    console.log('signup')
    clearErrors()
    firebase.auth().createUserWithEmailAndPassword(email, password).catch(err => {

        switch(err.code) {
        case "auth/email-already-in-use":
          setEmailError(err.message)
          break
        case "auth/invalid-email":
          setEmailError(err.message)
        break
        case "auth/weak-password":
          setPasswordError(err.message)
        break
        default: 
        setEmailError('Invalid')
      }
    })
    firebase.auth().onAuthStateChanged(user => {
      if(user) {
          user.updateProfile({
            displayName: name,
          }) 
          db.collection('users').doc(user.uid).set({
              created: new Date(), 
              msgids,
              uid: firebase.auth().currentUser.uid,
              online: true, 
              userinfo: {
                name,
                cover,
                age: '', 
                phone: '', 
                city: '',
                country: '',
                website: 'https://',
                job: '',
                email,
              },
              customization: {
                color: '#10325c',
                themecolor: '#0f6ce6',
                darkmode: false,
                widemode: false,
              }
          })
          db.collection('notifications').doc(user.uid).set({
            notifications: []
          })

      }//if (user)
      else {
        setUser('')
      } 
    }) 
  }
  const handleLogout = () => {
    if(user) {
      db.collection('users').doc(user.uid).update({online: false})
    }
    window.location.reload()
    firebase.auth().signOut()
  }
  const authListener = () => {
    firebase.auth().onAuthStateChanged(user => {
      if(user) {
        clearInputs()
        setUser(user)
        db.collection('users').doc(user.uid).update({online: true})
      }
      else {

          setUser('')
          db.collection('users').doc(user.uid).update({online: false})
      }
    })
  } 

  function loginwithGoogle(){
    var provider = new firebase.auth.GoogleAuthProvider();
    provider.addScope('email');

    firebase.auth()
    .signInWithPopup(provider)
    .then((result) => {
      if(result.additionalUserInfo.isNewUser){
            /** @type {firebase.auth.OAuthCredential} */
      var credential = result.credential;

      // This gives you a Google Access Token. You can use it to access the Google API.
      var token = credential.accessToken;
      // The signed-in user info.
      var user = result.user;
      // ...
      db.collection('users').doc(user.uid).set({
        created: new Date(), 
        msgids,
        uid: user.uid,
        online: true, 
        userinfo: {
          name: user.displayName,
          cover: user.photoURL,
          age: '', 
          phone: user.phoneNumber, 
          city:'',
          country: '',
          website: 'https://',
          job: '',
          email: user.email
        },
        customization: {
          color: '#10325c',
          themecolor: '#0f6ce6',
          darkmode: false,
          widemode: false,
        }
    })
    db.collection('notifications').doc(user.uid).set({
      notifications: []
    })
      }

    }).catch((error) => {
      // Handle Errors here.
      var errorCode = error.code;
      var errorMessage = error.message;
      // The email of the user's account used.
      var email = error.email;
      // The firebase.auth.AuthCredential type that was used.
      var credential = error.credential;
      // ...
      setEmailError(error.message)
      setTimeout(()=>{
        setEmailError('')
      },3000)
    });
  }
  function loginwithTwitter(){
    var provider = new firebase.auth.TwitterAuthProvider();
    provider.addScope("email");

    firebase.auth().signInWithPopup(provider)
    .then((result) => {
      if(result.additionalUserInfo.isNewUser){
        /** @type {firebase.auth.OAuthCredential} */
        var credential = result.credential;

        // This gives you a Google Access Token. You can use it to access the Google API.
        var token = credential.accessToken;
        // The signed-in user info.
        var usergoogle = result.user;
        // ...
        db.collection('users').doc(usergoogle.uid).set({
          created: new Date(), 
          msgids,
          uid: usergoogle.uid,
          online: true, 
          userinfo: {
            name: usergoogle.displayName,
            cover: usergoogle.photoURL,
            age: '', 
            phone: usergoogle.phoneNumber, 
            city:'',
            country: '',
            website: 'https://',
            job: '',
            email: usergoogle.email
          },
          customization: {
            color: '#10325c',
            themecolor: '#0f6ce6',
            darkmode: false,
            widemode: false,
          }
      })
      db.collection('notifications').doc(usergoogle.uid).set({
        notifications: []
      })
    }
    })
    .catch((error) => {
      // Handle Errors here.
      var errorCode = error.code;
      var errorMessage = error.message;
      // The email of the user's account used.
      var email = error.email;
      // The firebase.auth.AuthCredential type that was used.
      var credential = error.credential;

      // ...
      setEmailError(email)
      setTimeout(()=>{
        setEmailError('')
      },3000)
      console.log(errorCode)
    });
  }
  function loginwithFacebook(){
    var provider = new firebase.auth.FacebookAuthProvider();
    provider.addScope("email");

    firebase.auth()
    .signInWithPopup(provider)
    .then((result) => {
      if(result.additionalUserInfo.isNewUser){
        /** @type {firebase.auth.OAuthCredential} */
        var credential = result.credential;

        // This gives you a Google Access Token. You can use it to access the Google API.
        var token = credential.accessToken;
        // The signed-in user info.
        var usergoogle = result.user;
        // ...
        db.collection('users').doc(usergoogle.uid).set({
          created: new Date(), 
          msgids,
          uid: usergoogle.uid,
          online: true, 
          userinfo: {
            name: usergoogle.displayName,
            cover: usergoogle.photoURL,
            age: '', 
            phone: usergoogle.phoneNumber, 
            city:'',
            country: '',
            website: 'https://',
            job: '',
            email: usergoogle.email
          },
          customization: {
            color: '#10325c',
            themecolor: '#0f6ce6',
            darkmode: false,
            widemode: false,
          }
      })
      db.collection('notifications').doc(usergoogle.uid).set({
        notifications: []
      })
    }
    })
    .catch((error) => {
      // Handle Errors here.
      var errorCode = error.code;
      var errorMessage = error.message;
      // The email of the user's account used.
      var email = error.email;
      // The firebase.auth.AuthCredential type that was used.
      var credential = error.credential;

      // ...
      setEmailError(email)
      setTimeout(()=>{
        setEmailError('')
      },3000)
      console.log(errorCode)
    });
  }
  useBeforeunload(() => {
    if(user) {
      db.collection('users').doc(user.uid).update({online: false})
    }
  }) 
  useEffect(() => { 
    authListener()
    window.addEventListener('onbeforeunload', removeActiveStatus) 
    function removeActiveStatus() {
      if(user) {
        db.collection('users').doc(user.uid).update({online: false})
      }
    }
    if(user) {
      db.collection('users').doc(user.uid).update({online: true})
    }

  },[])  
  /*
   localStorage.openpages = Date.now();
   var onLocalStorageEvent = function(e){
       if(e.key == "openpages"){
           // Listen if anybody else is opening the same page!
           localStorage.page_available = Date.now();
       }
       if(e.key == "page_available"){
           alert("One more page already open");
       }
   };

   window.addEventListener('storage', onLocalStorageEvent, false);
*/  
   return ( 

        <Router >
    <div className="App">
       {user?
        <ContextAppProvider>
        <>
          <Body setLoading={setLoading} handleLogout={handleLogout} />
          <Redirect to='/Home'/>
        </>
        </ContextAppProvider>
        :
        <> 
        <Login loginwithTwitter={()=>loginwithTwitter} loginwithFacebook={()=>loginwithFacebook}loginwithGoogle={()=>loginwithGoogle} loading={loading} name={name} setName={setName} lname={lname} setlName={setlName}email={email} setEmail={setEmail} password= {password} setPassword={setPassword} handleLogin={handleLogin} handleSignup={handleSignup} hasAccount={hasAccount} setHasAccount={setHasAccount} emailError={emailError} passwordError={passwordError}/>       
        <Redirect to='/'/>
     </>
        }
    </div>  
     </Router>

  );
}

export default App
