import React, { useState, useEffect, useRef, useContext } from "react";
import firebase from "firebase";
//import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import { db,realtime_db, storage } from "../Fire";
/////////

/////////
import { CSSTransition, TransitionGroup } from "react-transition-group";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  NavLink,
  Redirect,
  useLocation
} from "react-router-dom";
import ReactTimeAgo from 'react-time-ago'
import emailjs from 'emailjs-com'
import Usersettings from "./Usersettings";
import { ContextApp } from "../ContextAPI";
import Hoverlink from "./Hoverlink";
import handleUpload from "../App"

////////////////////
const FileUpload = (props) => {  
  const { fileType, handleFile } = props; 
  const [fileList, setFileList] = useState([]);  
  const uploadFile = (e) => {    
     setFileList(e.target.files);    
     handleFile && handleFile(e.target.files);  
  };  
  const handleClose = (item) => {    
     const leftFiles = Array.prototype.filter.call(fileList, (i) => {      
        return i.name !== item.name;    
     });    
     setFileList(leftFiles);    
     handleFile(leftFiles);  
  };  
  return (    
     <div className="multiple_file_box">      
       <div className="mulfile_upload_container">        
           <label id="multifile_upload_button" htmlFor={`multifile_uploads`}>          
               <span><img src={UploadImage} />选择文件</span>          
               <input id={`multifile_uploads`} multiple  onChange={uploadFile} accept={fileType || "*"} type="file" />        
           </label>        
        <div className="multifile_preview" >          
       {            
           Array.from(fileList).map(item => {              
               return (                
                   <div className="multifile_item" key={item.lastModified}>                  
                       <span>{item.name}</span>                  
                       <span className="file_close" onClick={() => handleClose(item)}></span>                
                   </div>              
               );            
           })          
       }       
        </div>      
       </div>    
   </div>  
   );
};
//////////////////////

function Assigntask(props) {
  const { themecolor } = useContext(ContextApp)
  const user = firebase.auth().currentUser
  const [roleType, setRoleType] = useState('')
  const [cover, setCover] = useState("")
  const { handleLogout } = props
  const links = ["comment", "notifications", "settings", "adddraft", "logout"]
  const [notifi, setNotifLength]=useState(0)
  const lnks = [
    { icon: "fal fa-comment-alt", txt: "Comment" },
    { icon: "fal fa-bell", txt: "Notifications" },
    { icon: "fal fa-cog", txt: "Settings" },
    { icon: "fal fa-cog", txt: "Adddraft" },
    { icon: "fal fa-sign-out", txt: "Logout" }
  ];
  const id =db.collection('users').doc().id
  const lnksrow =
    lnks &&
    lnks.map((lnk) => {
      return (
        <Hoverlink  icon={lnk.icon} txt={lnk.txt} handleLogout={handleLogout} />
      );
    });

  const [textarea, setTextarea] = useState();
  const [inputs, setInputs] = useState({})
  const handleChange = (event) => {
    setInputs(event.target.value)
  }

  const handle_textarea_Change = (event) => {
    setTextarea(event.target.value)
  }


  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(inputs);

    var tutorialsRef = realtime_db.ref("/draft");
    tutorialsRef.push({
      username: user.displayName,
      subject: "",//inputs.subject,
      recipient: "",//inputs.recipient,
      cc: "",//inputs.CC,
      bcc: "",//inputs.BCC,
      message: "",//textarea,
      assistant: inputs,
      audiofile: "",
      status: "pending",
    });


  }

  useEffect(()=>{
    if (user){
    db.collection('users').doc(user.uid).onSnapshot(snap=>{
      const tmp = snap.data()
      setRoleType(tmp.role)
      console.log("home set role.." + tmp.role)
    })
    }
  },[])

  function determineTime() {
    const d = new Date();
    if (d.getHours() >= 6 && d.getHours() < 12) {
      return "Good Morning,";
    } else if (d.getHours() >= 12 && d.getHours() < 17) {
      return "Good Afternoon,";
    } else if (d.getHours() >= 17 && d.getHours() < 20) {
      return "Good Evening,";
    } else if (d.getHours() >= 20 && d.getHours() <= 23) {
      return "Good Night,";
    } else if (d.getHours() >= 0 && d.getHours() < 6) {
      return "Good Night,";
    }
  }

