import React, {useState, useEffect} from "react";
import "./styles.css";
import {db, Fire} from './Fire';
import firebase from "firebase";
import { BrowserRouter as Router,Switch,Route,Link,NavLink, Redirect } from "react-router-dom" 
import Sidebar from './components/Sidebar'
import Login from './components/Login'
import Body from './components/Body'
import adddraft from './components/adddraft'
import ContextAppProvider from './ContextAPI'
import { useBeforeunload } from 'react-beforeunload'
import emailjs from 'emailjs-com'

function App() {
  const [update, setUpdate]=useState(0)
  const [user, setUser]=useState('')
  const [name, setName]=useState('')
  const [email, setEmail]=useState('')
  const [password, setPassword]=useState('')
  const [emailError, setEmailError]=useState('')
  const [passwordError, setPasswordError]=useState('')
  const [hasAccount, setHasAccount]=useState(true)
  //const [loginType, setLoginType]=useState('assistant')
  var loginType = "assistant"
  const [lname, setlName]=useState('')
  const [userinfo, setUserinfo]=useState([])
  const [users, setUsers]=useState([])
  const [cover, setCover]=useState('https://www.gettyimages.ca/gi-resources/images/500px/983794168.jpg')
  const [forgotpassword, setForgotpassword]=useState(false)
  const [msgids, setMsgIds] = useState([''])
  const [loading, setLoading]=useState(false)
  const clearInputs = () => {
    setEmail('')
    setPassword('')
  }
  const clearErrors = () => {
    setEmailError('')
    setPasswordError('')
  }
  const handleLogin = () => {
    clearErrors()

    var checkbox = document.querySelectorAll("input[type='radio']:checked")
    for (let i = 0; i < checkbox.length; i++) {
        //setLoginType(checkbox[0].id)
        loginType = checkbox[0].id
        console.log("Checkbox is checked.." + loginType)
    }

    firebase.auth().signInWithEmailAndPassword(email, password)
    .then(()=>{setLoading(true)})
    .catch(err => {
      switch(err.code) {
        case "auth/invalid-email":
          setEmailError(err.message)
          break
        case "auth/user/disabled":
        case "auth/user-not-found":
          setEmailError('Email does not exist')
        break
        case "auth/wrong-password":
          setPasswordError('Incorrect Password')
        break
        default:
      }  
    })
  } 
  ////////////
  const handleUpload = () => {
    if (!file) {
        alert("Please upload an image first!");
    }

    const storageRef = ref(storage, `/files/${file.name}`);

    // progress can be paused and resumed. It also exposes progress updates.
    // Receives the storage reference and the file to upload.
    const uploadTask = uploadBytesResumable(storageRef, file);

    uploadTask.on(
        "state_changed",
        (snapshot) => {
            const percent = Math.round(
                (snapshot.bytesTransferred / snapshot.totalBytes) * 100
            );

            // update progress
            setPercent(percent);
        },
        (err) => console.log(err),
        () => {
            // download url
            getDownloadURL(uploadTask.snapshot.ref).then((url) => {
                console.log(url);
            });
        }
    );
  };
  ////////////
  const handleSignup = () => {
    console.log('signup')
    clearErrors()
    firebase.auth().createUserWithEmailAndPassword(email, password).catch(err => {
        
        switch(err.code) {
        case "auth/email-already-in-use":
          setEmailError(err.message)
          break
        case "auth/invalid-email":
          setEmailError(err.message)
        break
        case "auth/weak-password":
          setPasswordError(err.message)
        break
        default: 
        setEmailError('Invalid')
      }
    })
    firebase.auth().onAuthStateChanged(user => {
      if(user) {
          user.updateProfile({
            displayName: name,
          }) 
          db.collection('users').doc(user.uid).set({
              created: new Date(), 
              msgids,
              uid: firebase.auth().currentUser.uid,
              online: true,
              userinfo: {
                name,
                cover,
                age: '', 
                phone: '', 
                city: '',
                country: '',
                website: 'https://',
                job: '',
                email,
              },
              customization: {
                color: '#10325c',
                themecolor: '#0f6ce6',
                darkmode: false,
                widemode: false,
              }
          })
          db.collection('notifications').doc(user.uid).set({
            notifications: 'email'
          })
   
      }//if (user)
      else {
        setUser('')
      } 
    }) 
  }
  const handleLogout = () => {
    if(user) {
      db.collection('users').doc(user.uid).update({online: false})
    }
    //setLoginType('assistant')
    loginType = 'assistant'
    console.log("handle logout.." + loginType)
    window.location.host
    firebase.auth().signOut()
  }
  const authListener = () => {
    firebase.auth().onAuthStateChanged(user => {   
      if(user) {
        clearInputs()
        setUser(user)
        db.collection('users').doc(user.uid).update({online: true})

        firebase.auth().onAuthStateChanged(user => {
          db.collection('users').doc(user.uid).set({role: loginType})
          console.log("Final role.." + loginType)
       } )
      }
      else {
          setUser('')
          //setLoginType('assistant')
          //db.collection('users').doc(user.uid).update({online: false})
      }
    })
  } 
  
  function loginwithGoogle(){
    var checkbox = document.querySelectorAll("input[type='radio']:checked")
    for (let i = 0; i < checkbox.length; i++) {
        loginType = checkbox[0].id
        console.log("Checkbox is checked.." + loginType)
    }

    var provider = new firebase.auth.GoogleAuthProvider();
    provider.addScope('email');

    firebase.auth()
    .signInWithPopup(provider)
    .then((result) => {
      if(result.additionalUserInfo.isNewUser){
            /** @type {firebase.auth.OAuthCredential} */
      var credential = result.credential;
  
      // This gives you a Google Access Token. You can use it to access the Google API.
      var token = credential.accessToken;
      // The signed-in user info.
      var user = result.user;
      // ...
      db.collection('users').doc(user.uid).set({
        created: new Date(), 
        msgids,
        uid: user.uid,
        online: true, 
        userinfo: {
          name: user.displayName,
          cover: user.photoURL,
          age: '', 
          phone: user.phoneNumber, 
          city:'',
          country: '',
          website: 'https://',
          job: '',
          email: user.email
          
        },
        customization: {
          color: '#10325c',
          themecolor: '#0f6ce6',
          darkmode: false,
          widemode: false,
        }
    })
    db.collection('notifications').doc(user.uid).set({
      notifications: 'Google'
    })
      }

    }).catch((error) => {
      // Handle Errors here.
      var errorCode = error.code;
      var errorMessage = error.message;
      // The email of the user's account used.
      var email = error.email;
      // The firebase.auth.AuthCredential type that was used.
      var credential = error.credential;
      // ...
      setEmailError(error.message)
      setTimeout(()=>{
        setEmailError('')
      },3000)
    });
  }
  function loginwithFacebook(){
    var provider = new firebase.auth.FacebookAuthProvider();
    provider.addScope("email");

    firebase.auth()
    .signInWithPopup(provider)
    .then((result) => {
      if(result.additionalUserInfo.isNewUser){
        /** @type {firebase.auth.OAuthCredential} */
        var credential = result.credential;
  
        // This gives you a Google Access Token. You can use it to access the Google API.
        var token = credential.accessToken;
        // The signed-in user info.
        var usergoogle = result.user;
        // ...
        db.collection('users').doc(usergoogle.uid).set({
          created: new Date(), 
          msgids,
          uid: usergoogle.uid,
          online: true, 
          userinfo: {
            name: usergoogle.displayName,
            cover: usergoogle.photoURL,
            age: '', 
            phone: usergoogle.phoneNumber, 
            city:'',
            country: '',
            website: 'https://',
            job: '',
            email: usergoogle.email
          },
          customization: {
            color: '#10325c',
            themecolor: '#0f6ce6',
            darkmode: false,
            widemode: false,
          }
      })
      db.collection('notifications').doc(usergoogle.uid).set({
        notifications: 'Facebook'
      })
    }
    })
    .catch((error) => {
      // Handle Errors here.
      var errorCode = error.code;
      var errorMessage = error.message;
      // The email of the user's account used.
      var email = error.email;
      // The firebase.auth.AuthCredential type that was used.
      var credential = error.credential;
  
      // ...
      setEmailError(email)
      setTimeout(()=>{
        setEmailError('')
      },3000)
      console.log(errorCode)
    });
  }
  useBeforeunload(() => {
    if(user) {
